sections.css:
.s-social__link {
  display: inline-block;
  background-image: url(./var/www/my/swc.skm.pp.ua/src/components/blocks/social--round/img/sprite.png);
  width: 40px;
  height: 40px;
  margin: 5px;
  cursor: pointer;
  font-size: 0;
  outline: 0;
  opacity: 0.8;
  transition: opacity 0.3s ease;

  &:hover {
    opacity: 1;
  }

  &--fb {
    background-position: -124px -81px;
  }
  &--gp {
    background-position: 0px -143px;
  }
  &--tw {
    background-position: -172px 0px;
  }
  &--in {
    background-position: -172px -84px;
  }
  &--pi {
    background-position: -172px -42px;
  }
}

.s-button {
  display: inline-block;
  vertical-align: middle;
  color: #ffffff;
  position: relative;
  padding: 0 50px 0 25px;
  margin: 5px;
  font-size: 18px;
  line-height: 42px;
  border-radius: 4px;
  background-color: #666;
  text-decoration: none;
  border: 1px solid #fff;
  transition: all 0.2s ease-in-out;

  &::after {
    font-family: FontAwesome;
    padding-left: 20px;
    right: 25px;
    top: 0;
    position: absolute;
    min-width: 12px;
    transition: right 0.2s ease-in-out;
  }

  &:hover {
       background-color: #333;
       color: white;
    &::after {
      right: 15px;
    }
  }

  &--red {
    background-color: #ff2200;
    &:hover {
      background-color: #cc1b00;
    }
  }

  &--block {
    display: block;
    text-align: center;
  }

  &--small {
    padding: 0 45px 0 15px;
    font-size: 14px;
    line-height: 24px;
  }

  &--more:after {
    content: '\f061';
  }

  &--info:after {
    content: '\f05a';
  }

  &--cart:after {
    content: '\f07a';
  }

  &--link:after {
    content: '\f08e';
  }
}

